(249, 12, 3, '<p style=\"margin-left:2px; margin-right:2px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Perhatkan rangkaian listrik arus searah berikut :</span></span></p>\r\n\r\n<p style=\"margin-left:2px; margin-right:2px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><img id=\"Picture_x0020_1\" src=\"data:image/png;base64,\" style=\"height:128.25pt; width:150pt\" /> </span></span></p>\r\n\r\n<p style=\"margin-left:2px; margin-right:2px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika amperemeter A menunjukkan nilai 2 Ampere, besar sumber tegangan E adalah ..</span></span></p>', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">8,23 volt</span></span></p>', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">10,11 volt</span></span></p>', '<p>13,37&nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">volt</span></span></p>', '<p>18,24&nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">volt</span></span></p>', '<p>20,00&nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">volt</span></span></p>', 2, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><img id=\"Picture_x0020_2\" src=\"data:image/png;base64,\" style=\"height:131.25pt; width:168.75pt\" /> </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Vabd = Vabcd = Vacd </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">2.1 + (2+i<sub>1</sub>).2 = 2.1 + (-i<sub>1</sub>).1 + (i-2-i<sub>1</sub>).3 = (i-2).1 + (i-2-I<sub>1</sub>).3 maka :</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">2.1 + (2+i<sub>1</sub>).2 = 2.1 + (-i<sub>1</sub>).1 + (i-2-i<sub>1</sub>).3&nbsp; diperoleh</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-3i + 6i<sub>1</sub> = -10 -------(1)</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">2.1 + (-i<sub>1</sub>).1 + (i-2-i<sub>1</sub>).3 = (i-2).1 + (i-2-I<sub>1</sub>).3 diperoleh :</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">i + i<sub>1</sub> = 4&nbsp; ........(2)</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Persamaan (1) dan (2) diselesaikan diperoleh : i<sub>1</sub> = 2/9 A ; i = 34/9 A</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">E = i.1 + Vabd</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp; = 34/9.1 + (2.1 + (2+2/9).2) = 92/9 = 10,11 Volt</span></span></p>', '2020-12-10 00:21:26', '2020-12-10 00:22:09'),
(250, 12, 3, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:10.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\">Tiga logam jenis berbeda dengan ukuran sama disambung secara seri seperti gambar berikut :</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><img id=\"Picture_x0020_3\" src=\"data:image/png;base64,\" style=\"height:46.5pt; width:167.25pt\" /> </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika k adalah koefisien konduksi logam masing m-masing memenuhi k1 : k2 : k3 = 1 : 3 : 2 maka suhu pada titik persambungan logam 1_2 dan logan 2_3 adalah .......</span></span></p>', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">120<sup>o</sup> dan 80<sup>o</sup></span></span></p>', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">100<sup>o</sup> dan 80<sup>o</sup></span></span></p>', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">90<sup>o</sup> dan 70<sup>o</sup></span></span></p>', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">80<sup>o</sup> dan 70<sup>o</sup></span></span></p>', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">80<sup>o</sup> dan 60<sup>o</sup></span></span></p>', 3, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Sambungan seri setiap konduktor menghantarkan kalor yang sama setiap detiknya</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\"><img id=\"_x0000_i1025\" src=\"data:image/png;base64,\" style=\"height:27pt; width:55.5pt\" /> </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\"><img id=\"_x0000_i1025\" src=\"data:image/png;base64,\" style=\"height:15pt; width:66pt\" /> </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">A dan L dicoret maka :</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">1.∆t<sub>1</sub> = 3.∆t<sub>2</sub> = 2.∆t<sub>3</sub></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">∆t<sub>1</sub> : ∆t<sub>2</sub> : ∆t<sub>3</sub> = 6 : 2 : 3</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">∆t<sub>1</sub>=6/11.∆t<sub>tot</sub>=6/11.(150-40) = 60<sup>o</sup></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">(150-t<sub>12</sub>) = 60 <span style=\"font-family:Wingdings\">&agrave;</span> t<sub>12</sub>=90<sup>o</sup></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">∆t<sub>2</sub> = 2/11.110 = 20<sup>o</sup></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">(t<sub>12</sub>-t<sub>23</sub>) = 20</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">(90-t<sub>23</sub>) = 20</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">t<sub>23</sub> = 70<sup>o</sup></span></span></p>', '2020-12-10 00:26:12', '2020-12-12 20:28:23'),
